export const renderStyles= ()=>`

* {
    margin: 0;
    padding: 0;
}

body {
    position: relative;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    overflow-y: auto;
    background-color: black;
    font-family: 'Poppins', sans-serif;
}

body::after {
    content: "";
    background:
        url('data:image/jpeg;base64,');
    background-repeat: repeat;
    background-position: center;
    opacity: 0.3;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    z-index: -1;
}


.spacer {
    height: 100%;
}

.index-title {
    position: absolute;
    top: 450px;
    left: 50%;
    transform: translate(-50%, -50%);
    color: #ffffff;
    min-height: 300px;
    max-height: 300px;
    font-size: clamp(10px, 20vw, 300px);
    padding: 0;
    font-weight: 900;
    text-transform: uppercase;
    pointer-events: none;
    user-select: none;
    line-height: 1em;
    z-index: 1;
    text-align: center;
    transition: 0.5s all;

}


.index-main {
    margin: 0;
    display: flex;
    flex-flow: row wrap;
    background-color: black;
}

.index-main>div {
    flex-grow: 1;
    width: 10vw;
    height: 10vw;
    min-width: 300px;
    min-height: 300px;
    overflow: hidden;
    position: relative;
    background-color: black;
}

.index-main>div a {
    display: inline-block;
    width: 100%;
    height: 100%;
}

.index-main>div a>img {
    object-fit: cover;
    transition: all .5s;
}

.gallery-img-caption {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    transition: 0.5s;
    opacity: 0;
    background-color: rgba(0, 0, 0, 0);
    color: white;
    text-transform: capitalize;
    font-size: 90%;
    font-weight: 900;


}

.gallery-img-caption h1 {
    text-align: center;
    padding-left: 20px;
    padding-right: 20px;
}

.index-main>div:hover .gallery-img-caption {
    opacity: 1;
    background-color: rgba(0, 0, 0, .5);
}


.index-main>div:hover .gallery-img {
    transform: scale(1.4) rotate(10deg);
}

footer {
    margin-top: auto;
    background-color: rgba(0, 0, 0, 0.6);
    border-top: 1px solid rgba(255, 255, 255, 0.5);
    display: flex;
    min-height: 80px;
    font-size: 0.8rem;
    align-items: center;
    justify-content: center;
}

footer span {
    color: #e9e9e9;
    text-align: center;
    padding: 10px;
    color: white;
    font-weight: 300;

}


footer b {
    color: #e9e9e9;
}

footer a,
footer a:visited {
    color: white;
    font-weight: bold;

    text-decoration: none;
}

footer a:hover {
    text-decoration: underline;
}

/* //////////////////////////////////////////////////////////////////////////// DETAIL */
.detail-return {
    position: absolute;
    top: 9px;
    left: 10px;
    color: white;
    font-size: 1rem;
}

.detail-return a{
    padding:10px;
    text-decoration: none;
    color:white;
}
.detail-return a::before {
    position: absolute;
    content: '';
    top: -15px;
    left: -2px;
    font-size: 1.62rem;
}
.detail-main {
    padding: 20px;
    display: flex;
    flex-flow: column nowrap;
    letter-spacing: normal;
}

.detail-title {
    display: block;
    color: #ffffff;
    font-size: clamp(26px, 4vw, 60px);
    padding: 20px;
    font-weight: 900;
    text-transform: uppercase;
    pointer-events: none;
    user-select: none;
    line-height: 1em;
    z-index: 1;
    text-align: center;
    transition: 0.5s all;

}

.detail-image {
    display: flex;
    flex-flow: column;
    align-items: center;
    padding: 10px;
    max-width: 1280px;
    align-self: center;
    font-size: 1rem;
    font-weight: 300;

}



.detail-main figure>a>img {
    width: 1280px;
    max-width: 100%;
    height: auto;
    outline: 10px solid white;
    outline-offset: -10px;

}

figcaption {

    display: flex;
    align-items: center;
    padding: 10px;
    padding-bottom: 15px;
    background-color: white;
    color: black;
    margin-top: -10px;
    margin-bottom: 20px;
    flex-flow: column;
    position: relative;

}


figcaption span {
    font-weight: 400;
    font-style: italic;
    font-size: clamp(5px, 18pt, 0.8rem);
    text-align: center;
}

figcaption span:nth-child(odd) {
    font-weight: bold;
    border-bottom: 2px dotted black;
    margin-bottom: 6px;
    margin-top: 6px;

}

.detail-image>h1 {
    font-size: 1.5rem;
    width: 33%;
    line-height: 2;
    min-width: fit-content;
    align-self: flex-start;
    display: inline-block;
    padding-bottom: 5px;
    border-bottom: 4px solid white;
    margin-bottom: 20px;
}

.detail-image>p,
.detail-image>h1 {

    color: rgb(233, 233, 233);
    text-shadow: 2px 2px 4px black;
    line-height: 2rem;
    align-self: flex-start;

}
.detail-image>p{
    margin-bottom: 40px;
}
.detail-image>p::first-letter {
    font-weight:500;
    font-size: 160%;
    letter-spacing: 0.05rem;
    color: rgb(233, 233, 233);
}

.detail-image>p a,
.detail-image>p a:visited {
    color: white;
    font-weight: inherit;
    text-decoration: none;
    border-bottom: 2px dotted white;
}

.detail-image>p a:hover {
    /* color:rgb(130, 130, 255); */
    font-weight: inherit;
    text-decoration: none;
    border-bottom: 2px solid white;
}

.detail-image>p b {
    /* color:rgb(130, 130, 255); */
    font-weight: 700;
    font-size: 1em;
}

`